// Find assets of a department
var assetByDept = new GlideRecord('alm_hardware');
assetByDept.addQuery('department','=','221f79b7c6112284005d646b76ab978c'); // IT department 
assetByDept.addQuery('asset_tag','=','P1000540'); // P1000540

assetByDept.query();

// Write a log message for each Hardware Asset Record for Department
gs.info("+++++++++++++ Hardware asset run ++++++++++++++++++++++++");
gs.info("+++++++++++++ Row count ++++++++++++++++++++++++" + assetByDept.getRowCount());
